UPDATE Рекламный_блок SET Рекламный_блок.Продолжительность = (select CONVERT(TIME, DATEADD(SECOND, sum(datediff(second, '00:00:00', Рекламный_ролик.Продолжительность)), '00:00:00')) from Рекламный_ролик Join Состав_блока ON Рекламный_ролик.КодРолика = Состав_блока.КодРолика join Рекламный_блок ON Рекламный_блок.КодБлока=Состав_блока.КодБлока Where Рекламный_блок.КодБлока = 11) where Рекламный_блок.КодБлока = 11