UPDATE Плейлист SET Плейлист.Продолжительность = (select CONVERT(TIME, DATEADD(SECOND, sum(datediff(second, '00:00:00', Трек.Длительность)), '00:00:00')) from Трек Join Состав_Плейлиста ON Трек.КодТрека = Состав_Плейлиста.КодТрека join Плейлист ON Плейлист.КодПлейлиста=Состав_Плейлиста.КодПлейлиста Where Плейлист.КодПлейлиста = 13) where Плейлист.КодПлейлиста = 13